                    {
                        error = ARCONTROLLER_FEATURE_ARDrone3_RegisterARCommands (deviceController->aRDrone3);
                    }
                    if (error == ARCONTROLLER_OK)
                    {
                        error = ARCONTROLLER_Device_RegisterCallbacks (deviceController, deviceController->aRDrone3);
                    }
                    break;
                // If the remote controller on which we are connected is a ARDISCOVERY_PRODUCT_SKYCONTROLLER_2
                case ARDISCOVERY_PRODUCT_SKYCONTROLLER_2:
                    // register the feature aRDrone3
